const Header = (props) => {
    console.log(props);
    return (
        <h1>{props.course}</h1>
    )
}

const Part = (props) => {
    console.log(props);
    return (
        <p>
            {props.partName} {props.exerciseCount}
        </p>
    )
}

const Content = (props) => {
    console.log(props);
    return (
        <div>
            <Part partName={props.part1}
                  exerciseCount={props.exercises1} />
            <Part partName={props.part2}
                  exerciseCount={props.exercises2} />
            <Part partName={props.part3}
                  exerciseCount={props.exercises3} />
        </div>
    )
}

const Total = (props) => {
    console.log(props);
    return (
        <div>
            <p>
                Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}
            </p>
        </div>
    )
}

const App = () => {
    const course = "Half Stack application development";
    const parts = [
        {
            name: "Fundamentals of React",
            exercises: 10
        },
        {
            name: "Using props to pass data",
            exercises: 7
        },
        {
            name: "State of a component",
            exercises: 14
        }
    ];

    return (
        <div>
            <Header course={course} />
            <Content parts={parts} />
            <Total exercises1={exercises1} exercises2={exercises2} exercises3={exercises3} />
        </div>
    )
}

export default App
