const Header = (props) => {
    console.log("Header");
    console.log(props);
    return (
        <h1>{props.course}</h1>
    )
}

const Part = (props) => {
    console.log("Part");
    console.log(props);
    return (
        <p>
        </p>
    )
}

const Content = (props) => {
    console.log("Content");
    console.log(props);
    return (
        <div>
            <Part part={props.parts[0]} />
            <Part part={props.parts[1]} />
            <Part part={props.parts[2]} />
        </div>
    )
}

const Total = (props) => {
    console.log("Total");
    console.log(props);
    return (
        <div>
            <p>
                Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises}
            </p>
        </div>
    )
}

const App = () => {
    const course = "Half Stack application development";
    const parts = [
        {
            name: "Fundamentals of React",
            exercises: 10
        },
        {
            name: "Using props to pass data",
            exercises: 7
        },
        {
            name: "State of a component",
            exercises: 14
        }
    ];

    return (
        <div>
            <Header course={course} />
            <Content parts={parts} />
            <Total parts={parts} />
        </div>
    )
}

export default App
